pragma solidity ^0.8.0;

import {LibDiamond} from "../libraries/LibDiamond.sol";

struct Content {

      string contentName;

      address contentCreator;

      uint256 contentID;

      bool flagged;

      uint256 flaggedTimestamp;
      
      address flaggedBy;

      string reasonForFlagging;

      string contentDescription;

      bool isVotedOn;

      address[] voters;

      uint256 totalVoteCount;

      uint256 flaggedVotes;

      uint256 yesVotes;

      uint256 noVotes;

}

struct flaggedContent {

      uint256 contentID;

      uint256 flaggedTimestamp;

      address flaggedBy;

}

struct VotersDetails {

      address voterAddress;

      bool hasVoted;

      bool vote;

      bool verified;
      
}


struct AppStorage {

    // FlagContent Facet

    address _tokenAddress;

    address _nftAddress;

    uint256 _totalFlaggedContent;

    uint256 _voteCount;

    uint256 _votingTime;

    uint256 _totalVoters;

    bool _isVotingOn;

mapping(uint256 => Content) ContentBank;

// mapping(address => bool) EligibleVoters;

address[] EligibleVoters;

mapping (address => VotersDetails) Voters;

mapping(uint256 => flaggedContent) flaggedContents;

}

contract Modifiers {
    modifier onlyOwner() {
        address owner = LibDiamond.contractOwner();
        require(owner == msg.sender, "Only Owner can call this function");
        _;
    }  
}

